import React, { useState, useEffect } from "react";
import CommonSection from "./../shared/CommonSection";
import { Container, Row, Col } from "reactstrap";
import { useLocation } from "react-router-dom";
import TourCard from "../shared/TourCard";
import Newsletter from "./../shared/Newsletter";

const SearchResultList = () => {
    const location = useLocation();
    const [data, setData] = useState(location.state || []);
    const [loading, setLoading] = useState(!location.state); // Loading only if no initial data

    useEffect(() => {
        if (!location.state) {
            // Extract query parameters from the URL
            const searchParams = new URLSearchParams(location.search);
            const city = searchParams.get("city");
            const distance = searchParams.get("distance");
            const maxGroupSize = searchParams.get("maxGroupSize");

            const fetchData = async () => {
                try {
                    const response = await fetch(
                        `http://localhost:4000/tours/search/getTourBySearch?city=${city}&distance=${distance}&maxGroupSize=${maxGroupSize}`
                    );
                    const result = await response.json();

                    if (result.success) {
                        setData(result.data);
                    } else {
                        setData([]);
                    }
                } catch (error) {
                    console.error("Error fetching tours:", error);
                    setData([]);
                } finally {
                    setLoading(false);
                }
            };

            fetchData();
        }
    }, [location.state, location.search]);

    return (
        <>
            <CommonSection title={"Tour Search Result"} />
            <section>
                <Container>
                    <Row>
                        {loading ? (
                            <h4 className="text-center">Loading...</h4>
                        ) : data.length === 0 ? (
                            <h4 className="text-center">No tour found</h4>
                        ) : (
                            data.map((tour) => (
                                <Col lg="3" className="mb-4" key={tour._id}>
                                    <TourCard tour={tour} />
                                </Col>
                            ))
                        )}
                    </Row>
                </Container>
            </section>
            <Newsletter />
        </>
    );
};

export default SearchResultList;
